session.css:
.button-container, file-container, .menu-container{cursor:pointer}
.button-container label{border:none}
[class^="fa fa-fw fa-wav"], [class^="fa fa-fw fa-btn"], [class^="fa fa-fw fa-step"], .file-container .icon{
width:100%;
height:100%;
background-repeat:no-repeat;
background-position:center;
}
.fa-btn_play {background-image: url("/Interface/icones/play.svg")}
.on .fa-btn_play {background-image: url("/Interface/icones/stop.svg")}
.fa-btn_on {background-image: url("/Interface/icones/on_0.svg")}
.on .fa-btn_on {background-image: url("/Interface/icones/on_1.svg")}
.fa-btn_info {background-image: url("/Interface/icones/info.svg")}
.fa-btn_revert {background-image: url("/Interface/icones/revert.svg")}
.fa-btn_help {background-image: url("/Interface/icones/help.svg")}
.fa-btn_rec {background-image: url("/Interface/icones/record.svg")}
.on .fa-btn_rec {background-image: url("/Interface/icones/recording.svg")}
.fa-btn_pgn {background-image: url("/Interface/icones/page_n.svg")}
.fa-btn_pgp {background-image: url("/Interface/icones/page_p.svg")}
.fa-btn_pga {background-image: url("/Interface/icones/page_a.svg")}
.fa-btn_pgd {background-image: url("/Interface/icones/page_d.svg")}
.fa-btn_lock {
position:absolute;
background-image: url("/Interface/icones/lock_0.svg")
}
.on .fa-btn_lock {background-image: url("/Interface/icones/lock_1.svg")}
.fa-btn_lock+i {
position: relative;
z-index:1;
top: 25%
}
.fa-btn_cam {
display:flex
position:absolute;
top:0;left:0;bottom:0;right:0;
background-image: url("/Interface/icones/cam_0.svg");
align-items: center;
z-index: -1;
}
.fa-btn_cam+i {
position: relative;
z-index:1;
top: 25%
}
.on .fa-btn_cam {background-image: url("/Interface/icones/cam_1.svg")}
.fa-btn_ers1 {background-image: url("/Interface/icones/erase_1.svg")}
.fa-btn_ers2 {background-image: url("/Interface/icones/erase_2.svg")}
.fa-btn_ers3 {background-image: url("/Interface/icones/erase_3.svg")}
.fa-reverse::after {content: '►'}
.on .fa-reverse::after {content: '◄'}

